import BigCardStyle from './BigCard.module.css'
import logoRoma from '../img/logo-roma.png'
import { useLoaderData } from 'react-router-dom'
import Classifica from './Classifica';

const stileTi = BigCardStyle.stileTi 
// const stileSel = BigCardStyle.stileSel
// const stileSel1 = BigCardStyle.stileSel1

const stileTi1 = BigCardStyle.stileTi1



const BigCard = () => {

  const data = useLoaderData('total-league')

  const lega = data.response

  console.log(lega)


return(
<>
<div className={`d-none d-sm-block bg-danger py-4 ${BigCardStyle.TitleSCard}`}>

<div className='container p-0'>
<div className='col-12 p-2'>
{lega.map( dato =>
<div className='bg-light d-flex flex-wrap p-2 rounded shadow'>

<div className='col-12 d-flex mb-3'>
<div className='col-6 px-3'>
<div className={stileTi}>Campionato</div>
<div className='d-flex'>
<div><img style={{'width': '40px'}} src={dato.league.logo} alt='logo'></img></div> 
<div className='ps-3'>Serie A</div>
</div>
</div> 

<div className='px-3 col-6'>
<div className={stileTi}>Nazione</div>
<div>{dato.country.name}</div>
</div>

</div>


<Classifica></Classifica>

</div> 
)}  
</div>
</div>
</div>

<div className={`d-block d-sm-none bg-danger py-4 ${BigCardStyle.TitleSCard1}`}>

<div className='container p-0'>
<div className='col-12 p-2'>
{lega.map( dato =>
<div className='bg-light d-flex flex-wrap p-2 rounded shadow'>

<div className='col-12 d-flex mb-3'>

<div className='col-6'>
<div className={stileTi1}>Campionato</div>
<div className='d-flex'>
<div><img style={{'width': '20px'}} src={dato.league.logo} alt='logo'></img></div> 
<div className='ps-3'>{dato.league.name}</div>
</div>
</div> 

<div className='px-3 col-6'>
<div className={stileTi1}>Nazione</div>
<div>{dato.country.name}</div>
</div>

{/* <div className='px-3 col-3'>
<div className={stileTi1}>Squadre</div>
<div>20</div>
</div> */}

</div>

<Classifica></Classifica>

</div>  
)}  
</div>
</div>
</div>
</>
)};


export default BigCard;





export const loader = async ( {params} ) => {

  const Id = params.idCampo;

const paraId = Id.toString()

const response = await fetch(" https://v3.football.api-sports.io/leagues?id=" + paraId, {
  "method": "GET",
  "headers": {
    "x-rapidapi-host": "v3.football.api-sports.io",
    "x-rapidapi-key": "353b6d4834b3e467f904d41caf26e4fc"
  }
})

if(!response.ok){
//    
}else{
// const data = await response.json()
console.log(response)
return response
}}
