import styleSCard from './SmallCard.module.css'
import ImgTotti from '../img/totti.png'
import iconTeam from '../img/squadre.png'
import iconCamp from '../img/campionati.png'
import iconPos from '../img/icon-position.png';
import { useLoaderData } from 'react-router-dom';

const SmallCard = () => {

const data = useLoaderData()

const leagues = data.response

console.log(leagues)

return(
<div>
<div className='container p-0 d-flex flex-wrap justify-content-center'>
{ leagues.map( lega  => 
<div className='col-md-6 col-lg-3 p-2 d-flex'>
<div className='bg-light p-2 rounded shadow'>
<div><img className='img-fluid' src={ImgTotti} alt="ImgTotti"></img></div>
{/* <div className={`text-center mt-2 ${styleSCard.resLabel}`} mt-2>Francesco</div> */}
<h2 className={`mt-2text-center ${styleSCard.TitleSCard}`}>{lega.league.name}</h2>
<div className='d-flex align-items-center mb-4 ms-2'>
<div className={styleSCard.boxIcon}>
<img src={iconPos} className={styleSCard.resImg} alt="img"></img></div>
<div className='ps-3'>As Roma</div>
</div>
<div className='d-flex align-items-center ms-2 mb-2'>
<div className={`d-flex  justify-content-center ${styleSCard.boxIcon}`}>
<img src={iconCamp} className={styleSCard.resImg1} alt="img"></img></div>
<div className='ps-3'>Serie A</div>
</div>
</div>
</div>
)}
</div>
</div>
    )};

export default SmallCard;
