import BigCardStyle from './BigCard.module.css'
import { useLoaderData } from 'react-router-dom'
import Classifica from './Classifica';

const stileTi = BigCardStyle.stileTi 
// const stileSel = BigCardStyle.stileSel
// const stileSel1 = BigCardStyle.stileSel1

const stileTi1 = BigCardStyle.stileTi1



const BigCard = () => {

  const data = useLoaderData('total-league')

  const lega = data.response

  console.log(lega)

  const NomeCampionato = data.response[0].league.name

  const IdCampionato = data.response[0].league.id

  const stagioni = data.response[0].seasons

  console.log(stagioni)


  /////
  const id = props.IdCampionato

  // const lega = props.NomeCampionato

   console.log(id, 'verifica id')


     const [datiClassifica, changeDatiClassifica] = useState([])

     const FdatiClassifica = async (event) => {

       const data = event.target.value

       console.log(data)

         const response = await fetch("https://v3.football.api-sports.io/standings?league=" + id + "&season=" + data, {
         "method": "GET",
         "headers": {
         "x-rapidapi-host": "v3.football.api-sports.io",
         "x-rapidapi-key": "353b6d4834b3e467f904d41caf26e4fc"
         }
         })

         if(!response.ok){
        ///
         }else if(data === '' && response.ok){
         ///
         }else{
         const data = await response.json()
         console.log(data.response, 'dati')

         const filtraSezioneClassifica = data.response[0].league.standings[0]

         console.log(filtraSezioneClassifica)

         changeDatiClassifica(filtraSezioneClassifica)
         }} 

     // const stileTi = ClassificaStyle.stileTi 
     // const stileSel = BigCardStyle.stileSel
     // const stileSel1 = BigCardStyle.stileSel1

     const stileTi1 = ClassificaStyle.stileTi1
  /////



return(
<>
<div className={`d-none d-sm-block bg-danger py-4 ${BigCardStyle.TitleSCard}`}>

<div className='container p-0'>
<div className='col-12 p-2'>

<div className='bg-light d-flex flex-wrap p-2 rounded shadow'>
{lega.map( dato =>
<div className='col-12 d-flex mb-3'>
<div className='col-6 px-3'>
<div className={stileTi}>Campionato</div>
<div className='d-flex'>
<div><img style={{'width': '40px'}} src={dato.league.logo} alt='logo'></img></div> 
<div className='ps-3'>{dato.league.name}</div>
</div>
</div> 

<div className='px-3 col-6'>
<div className={stileTi}>Nazione</div>
<div>{dato.country.name}</div>
</div>

</div>
)}

<div className='col-12 d-flex align-items-center'>
<div className='col-6 px-2'>Classifica finale</div>
<div className='col-6 px-2'>
<select onChange={FdatiClassifica} className={`form-select`} aria-label="Default select example">
{/* <option selected value=''>Stagione</option>
{props.stagioni.map(dato =>
<option value={dato.year}>{dato.year}</option>
)} */}
</select>
</div>
</div>

<Classifica
IdCampionato={IdCampionato}
NomeCampionato={NomeCampionato}
datiClassifica={datiClassifica}
></Classifica>


</div> 

</div>
</div>
</div>

<div className={`d-block d-sm-none bg-danger py-4 ${BigCardStyle.TitleSCard1}`}>

<div className='container p-0'>
<div className='col-12 p-2'>
{lega.map( dato =>
<div className='bg-light d-flex flex-wrap p-2 rounded shadow'>

<div className='col-12 d-flex mb-3'>

<div className='col-6'>
<div className={stileTi1}>Campionato</div>
<div className='d-flex'>
<div><img style={{'width': '20px'}} src={dato.league.logo} alt='logo'></img></div> 
<div className='ps-3'>{dato.league.name}</div>
</div>
</div> 

<div className='px-3 col-6'>
<div className={stileTi1}>Nazione</div>
<div>{dato.country.name}</div>
</div>

{/* <div className='px-3 col-3'>
<div className={stileTi1}>Squadre</div>
<div>20</div>
</div> */}

</div>

<Classifica
IdCampionato={IdCampionato}
NomeCampionatoo={NomeCampionato}
></Classifica>

</div>  
)}  
</div>
</div>
</div>
</>
)};


export default BigCard;





