import { useState } from 'react';
import styleFilter from './FilterCamp.module.css'
import { event } from 'jquery';


const FilterCamp = (props) => {


 const [stateSelect, changeSelect] = useState('')   

 const [statoNazione, changeNazione] = useState('')

const OnMetod = (event) => {
if(event.target.value === statoNazione){
changeSelect('10')   
}else{
changeSelect('1')
}    
}

// onFocus={() => changeSelect('10')}

return(
<>
<div className={`text-center`}>
<div className='container justify-content-center d-flex flex-wrap p-0 mt-4'>
<div className='col-12 col-lg-6 p-2'>

<div className='bg-light p2 rounded shadow'>

<div className='d-flex flex-wrap justify-content-center'>
<div className='col-12 col-lg-6 p-2'>
<div className={styleFilter.TestoLabel}>NAZIONE</div>
<select size={stateSelect} onFocus={OnMetod} onBlur={() => changeSelect('1')} onChange={(event) => {props.filterSelect(event.target.value);changeSelect('1'); changeNazione(event.target.value)}} class="form-select border-black" aria-label="Default select example">
<option value='tutti' selected>Tutti</option>
{ props.countryFiltrati.map( country => 
 <option value={country}>{country}</option>   
)
}
</select>
</div>
</div>

{/* <div className='mt-3 p-2'>
<button type="button" class={`btn btn-primary w-100 p-0 ${styleFilter.TitleBtn}`}>Filtra</button>
</div> */}

</div>
</div>
</div>
</div>
</>
)};

export default FilterCamp;
