import stileCamp from './Campionato.module.css'
import BgCampionato from '../img/bg-campionato.png'
import FilterCamp from '../components/FilterCamp';
import SmallCard from '../components/SmallCard';

import { useRouteLoaderData } from "react-router-dom";

const Campionati= () => {

const BgStyle = stileCamp.BgStyle
const TitleStyle = stileCamp.stileTitle


const data = useRouteLoaderData('campionati')

const detailCampionati = data.response

return(
<>
<div className="container-fluid p-0">
<img src={BgCampionato} className="d-flex d-lg-none img-fluid" alt="sfondo-home"></img>
<img src={BgCampionato} className="d-none d-lg-flex img-fluid" alt="sfondo-home"></img>
</div>
<div className={BgStyle}>
<div className='d-flex justify-content-center py-4 mx-2 text-center'>
<h2 className={TitleStyle}>SCOPRI LE STATISTICHE SU VARI CAMPIONATI NEL MONDO</h2>

</div>
<FilterCamp></FilterCamp>
<SmallCard
detailCampionati = {detailCampionati}
></SmallCard>
</div>
</>
)};

export default Campionati;



export const loader = async ({request, params}) => {

const id = params.idNation;

const response = fetch("https://v3.football.api-sports.io/leagues" + id, {
	"method": "GET",
	"headers": {
		"x-rapidapi-host": "v3.football.api-sports.io",
		"x-rapidapi-key": "353b6d4834b3e467f904d41caf26e4fc"
	}
})  

if(!response.ok){
// throw json({message: 'nessun evento trovato'},{status: 500})
}else{
return response 
}
}
